    line = re.sub(r'\\index\[KT\]{(\d+)@([^!]+)!(\d+)\|(\d+) @(\d+):(\d+)}, (\d+)([^\\|\d])', r'\\index[KT]{\1@\2!\3|\4 @\5:\6}, \7\\index[KT]{\1@\2!0\5|0\7 @\5:\7}\8', line)
    line = re.sub(r'\\index\[KT\]{(\d+)@([^!]+)!(\d+)\|(\d+) @(\d+):(\d+)}, (\d+)([^\\|\d])', r'\\index[KT]{\1@\2!\3|\4 @\5:\6}, \7\\index[KT]{\1@\2!0\5|0\7 @\5:\7}\8', line)
